__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on Living Room Fan when garage motion at night",
    "parent": 0,
    "comment": "Turn on fan and set backlight when garage motion detected between 22:00 and 06:00",
    "if": [
      { "==": { "device": "4F 94 3D 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "3B E4 B8 1", "command": "DON" },
      { "device": "3B E4 B8 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "COS trigger: when Garage Motion ST becomes On during the overnight window, the routine turns on the Living Room Fan and sets its backlight to 30% for nighttime visibility."
  }
}
__END_NUCORE_ROUTINE__
