__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening fast-on sets Kitchen Table B warm or effect",
    "parent": 0,
    "comment": "If user triggers DFON on Kitchen Table A during evening window, set B to warm white 80%; otherwise run color effect on B",
    "if": [
      { "(": 1 },
        { "==": { "device": "n001_c82b964292bc", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:59", "day": 0 } } }
    ],
    "then": [
      {
        "device": "n001_c82b96429ae9",
        "command": "SET_LEVEL",
        "parameters": [
          { "name": "WW", "value": 80, "uom": "51", "precision": 1 }
        ]
      }
    ],
    "else": [
      {
        "device": "n001_c82b96429ae9",
        "command": "SET_EFFECT",
        "parameters": [
          { "name": "EFF", "value": 37, "uom": "25", "precision": 0 },
          { "name": "SPD", "value": 30, "uom": "51", "precision": 1 }
        ]
      }
    ],
    "reasoning": "COC on Kitchen Table A (DFON) is the trigger; restrict to an evening time window using a time range. If it occurs during 18:00-23:59 set Kitchen Table B to warm white at 80% using SET_LEVEL (WW param). If DFON occurs outside that window, run the seven color cross fade effect on Kitchen Table B with speed 30. Uses only commands and parameter UOMs defined in device structure."
  }
}
__END_NUCORE_ROUTINE__
