__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion triggers multilevel query",
    "parent": 0,
    "comment": "Query multilevel sensor when motion starts during night hours",
    "if": [
      { "==": { "device":"ZY014_380","status":"ST","value":100,"uom":"78","precision":0 } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"ZY014_118","command":"QUERY","parameters":[] }
    ],
    "else": [],
    "reasoning": "When the motion sensor (ZY014_380) changes to On (COS) during the defined night window (22:00-06:00), issue a QUERY to the multilevel sensor (ZY014_118) to capture temperature and luminance immediately. Using QUERY is required because devices only accept QUERY in the provided structure."
  }
}
__END_NUCORE_ROUTINE__
