__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Query fan and porch when treadmill turns on",
    "parent": 0,
    "comment": "When Office Treadmill 1 changes to On, query Office Fan 0 and LR Porch B",
    "if": [
      { "==": { "device": "n003_ssi29d54612", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "n003_ssi3b374ae3", "command": "QUERY", "parameters": [] },
      { "device": "n003_ssi1901527", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COS trigger: detect treadmill state change to On (ST==100). Actions are limited to the device's accepted command (QUERY), so query the fan and porch immediately to refresh their statuses for dashboards or follow-up automations."
  }
}
__END_NUCORE_ROUTINE__
