__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Afternoon: cool when hot",
    "parent": 0,
    "comment": "Between 14:00 and 20:00, if room temp >=75.0°F then set cool to 74°F and fan On.",
    "if": [
      { "from": { "time": "14:00", "to": { "time": "20:00", "day": 0 } } },
      { "and": 1 },
      { ">=": { "device": "n001_t511894001881", "status": "ST", "value": 750, "uom": "17", "precision": 1 } }
    ],
    "then": [
      { "device": "n001_t511894001881", "command": "CLISPC", "parameters": [ { "name": "n/a", "value": 740, "uom": "17", "precision": 1 } ] },
      { "device": "n001_t511894001881", "command": "CLIFS", "parameters": [ { "name": "n/a", "value": 1, "uom": "68", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Limit to afternoon hours (14:00-20:00). Trigger is temperature COS >=75.0°F (value 750 with precision 1). Then set Cool Setpoint to 74°F (740 precision 1) and set Fan Mode to On (enum value 1, uom 68)."
  }
}
__END_NUCORE_ROUTINE__
