__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunrise+5m Zone9 Watering when enabled and controller online",
    "parent": 0,
    "comment": "Start Zone 9 for 20 minutes at sunrise+5 minutes, only if Enabled and controller online",
    "if": [
      { "at": { "sunrise": 300 } },
      { "and": 1 },
      { "==": { "device": "n003_083a88eb65239", "status": "GV0", "value": 1, "uom": "2", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "n015_controller", "status": "ST", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n003_083a88eb65239", "command": "START", "parameters": [ { "name": "n/a", "value": 20, "uom": "45", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Schedule at sunrise+5min ensures morning watering. Require the zone Enabled (GV0==1) and the controller online (ST==1) to avoid starting when disabled or controller offline. Use START with 20 minutes (uom 45)."
  }
}
__END_NUCORE_ROUTINE__
