__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Raise backup reserve on low battery during work hours",
    "parent": 0,
    "comment": "When battery low during weekdays increase backup reserve and update data",
    "if": [
      { "<": { "device": "n005_2417880", "status": "GV0", "value": 20 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "n005_setup_2417880", "command": "BACKUP_PCT", "parameters": [ { "name": "n/a", "value": 30 } ] },
      { "device": "n005_2417880", "command": "UPDATE", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Monitor Remaining Battery (GV0) on Towers of Power; during weekday work hours if it drops below 20% set the Setup PW Backup Reserve to 30% using BACKUP_PCT and then refresh system data with UPDATE to ensure values are current. Uses only commands and properties defined in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
