__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning: sync Valve4 when Valve5 opens",
    "parent": 0,
    "comment": "When Valve 5 turns On between 06:00-08:00 on weekdays, open Valve 4 and beep Valve 5; else close Valve 4.",
    "if": [
      { "==": { "device": "38 7F 44 5", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "08:00" } } } }
    ],
    "then": [
      { "device": "38 7F 44 4", "command": "DON" },
      { "device": "38 7F 44 5", "command": "BEEP" }
    ],
    "else": [
      { "device": "38 7F 44 4", "command": "DOF" }
    ],
    "reasoning": "Trigger on a COS for Valve 5 status == On combined with a weekday time window. Then open Valve 4 and signal Valve 5; otherwise close Valve 4 to ensure default off state outside the window. Actions use existing DON/DOF/BEEP commands on the respective devices."
  }
}
__END_NUCORE_ROUTINE__
