__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Open Valve7 when Tank Wet On or FastOn during weekdays 09-17",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
        { "==": { "device": "55 2 A9 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "55 2 A9 2", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "38 7F 44 7", "command": "DON" }
    ],
    "else": [
      { "device": "55 2 A9 2", "command": "BEEP" }
    ],
    "reasoning": "Trigger on the tank becoming wet (ST=On) or a Fast On control (DFON) from the tank, but only during weekday business hours; when triggered turn on Valve 7; if the combined condition is not met, beep the tank as a notification."
  }
}
__END_NUCORE_ROUTINE__
