__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Physical DFON -> Turn on Dining and Kitchen via Backlight",
    "parent": 0,
    "comment": "Trigger from physical fast-on of Dining Room slider: set backlight high on Dining and Kitchen and persist.",
    "if": [
      { "==": { "device": "2A 4 E5 5", "control": "DFON" } }
    ],
    "then": [
      { "device": "2A 4 E5 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 120, "uom": "25", "precision": 0 } ] },
      { "device": "2A 4 E5 5", "command": "WDU", "parameters": [] },
      { "device": "2A 4 E5 6", "command": "BL", "parameters": [ { "name": "n/a", "value": 120, "uom": "25", "precision": 0 } ] },
      { "device": "2A 4 E5 6", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC 'DFON' from the Dining Room device as the human control trigger. Actions use the devices' accepted Backlight (BL) command with a high value within the documented 0-127 range and then Write Changes (WDU) to persist. No assumptions about send-command usage are made; only accepted commands are used."
  }
}
__END_NUCORE_ROUTINE__
