__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Heartbeat fast-on sets Sophia warm 3000K",
    "parent": 0,
    "comment": "When Heartbeat DFON occurs, set Sophia to 3000K if reachable; otherwise query Heartbeat",
    "if": [
      { "==": { "device": "55 2 A9 4", "control": "DFON" } },
      { "and": 1 },
      { "==": { "device": "n001_8801027c442d0b", "status": "GV6", "value": 1, "uom": "2" } }
    ],
    "then": [
      {
        "device": "n001_8801027c442d0b",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 200, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [
      {
        "device": "55 2 A9 4",
        "command": "QUERY",
        "parameters": []
      }
    ],
    "reasoning": "COC trigger: detect Heartbeat fast-on (DFON). Only act to set the bulb when the bulb is reachable (GV6 == 1). If not reachable, query Heartbeat to surface status for troubleshooting. Uses SET_CTBR parameters K (Kelvin), D (transition ms), and BR (brightness) with values within device ranges."
  }
}
__END_NUCORE_ROUTINE__
