__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query alarms when intrusion becomes ON",
    "parent": 0,
    "comment": "On COS of Intrusion Alarm -> Query Home Security and System Alarm",
    "if": [
      { "==": { "device": "ZY009_172", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY009_308", "command": "QUERY", "parameters": [] },
      { "device": "ZY009_310", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "This routine listens for a COS event where the Intrusion Alarm (ZY009_172) status (ST) becomes On (enum value 100). When that happens it triggers Query commands to both the Home Security Alarm (ZY009_308) and System Alarm (ZY009_310) so their current states can be retrieved. No alternative actions are required."
  }
}
__END_NUCORE_ROUTINE__
