__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: turn garage plug off if on and hardware alarm off",
    "parent": 0,
    "comment": "At sunset, if garage plug is On and Schlage hardware failure alarm is Off, turn garage plug Off to avoid leaving it powered.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY013_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY002_174", "status": "ST", "value": 0, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY013_1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "User wants the garage plug off at sunset if it's on and no hardware failure is signaled; check garage plug ST == On (uom 78) and Schlage hardware failure ST == Off, then send DOF to garage plug."
  }
}
__END_NUCORE_ROUTINE__
