__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening keypad activation sets basement to 60% with 30s ramp",
    "parent": 0,
    "comment": "Activate parent when either child is physically turned on during weekday evenings",
    "if": [
      { "(": 1 },
        { "==": { "device": "33 F 75 2", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "33 F 75 3", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "33 F 75 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "33 F 75 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When a child keypad is physically pressed (DFON) during weekday evenings, set the parent on level to 60% and apply the 30.0s ramp (enum index 18) to produce a smooth fade-in."
  }
}
__END_NUCORE_ROUTINE__
