__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "FR_Kpd-2.G press -> Fireplace 30% with 1min ramp",
    "parent": 0,
    "comment": "When the FR_Kpd-2.G is physically turned on, set fireplace lights to 30% using 1.0 minute ramp",
    "if": [
      { "==": { "device": "4A 97 EC 7", "control": "DON" } }
    ],
    "then": [
      {
        "device": "24 96 CA 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 12, "uom": "25", "precision": 0 }
        ]
      },
      {
        "device": "24 96 CA 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 30, "uom": "51", "precision": 0 }
        ]
      },
      {
        "device": "24 96 CA 1",
        "command": "DON",
        "parameters": [
          { "name": "n/a", "value": 30, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "COC trigger: FR_Kpd-2.G emits a DON when physically pressed. On that event set Fireplace_Lights Ramp Rate to the enum index for 1.0 minute (12), set On Level to 30%, then send DON with 30% to ensure the level is applied. Actions use device IDs and UOMs defined in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
