      { "and": 1 },
      { "weekly": { "days": "mon,wed,fri", "from": { "time": "06:00", "to": { "time": "08:00" } } } }
    ],
    "then": [
      { "device": "9 FC DA 8", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When Sprinkler_Zone7 changes to Off during the morning watering schedule, stop the pump to conserve water."
  }
}
