__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night tap: Output-010 triggers Output-011 for 120s",
    "parent": 0,
    "comment": "If Output-010 is turned on during overnight hours, pulse Output-011 for 2 minutes",
    "if": [
      { "==": { "device": "n005_output_10", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_output_11", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "Use COC detection for Output-010 physical ON (control DON) and restrict to overnight interval (22:00->06:00 day span). Action uses the device's Turn On For command with seconds parameter per DEVICE STRUCTURE (uom 58)."
  }
}
__END_NUCORE_ROUTINE__
