__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual Output-013 -> Output-014 for 30s",
    "parent": 0,
    "comment": "When Output-013 is manually turned on overnight, activate Output-014 for 30 seconds.",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "n005_output_13", "control": "DON" } }
    ],
    "then": [
      { "device": "n005_output_14", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 30, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a control event (manual On) from Output-013 as the trigger, restrict to overnight hours, and use the Turn On For command on Output-014 with a 30-second parameter (uom 58)."
  }
}
__END_NUCORE_ROUTINE__
