__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evenings: A DFON -> set D and C to 100%",
    "parent": 0,
    "comment": "Trigger on MiniRemote - A DFON during weekday evenings; set D and C status to 100% via WDU",
    "if": [
      { "(": 1 },
        { "==": { "device": "3F 0 2B 2", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "3F 0 2B 3", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "3F 0 2B 4", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A DFON is a COC event; restrict to weekday evenings using a weekly schedule and then write ST=100 (uom 51) to devices D and C using their accepted WDU command."
  }
}
__END_NUCORE_ROUTINE__
