__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "D DFON -> Set C on weekdays 09-17",
    "parent": 0,
    "comment": "When D emits DFON during work hours, write change to C",
    "if": [
      { "==": { "device": "70 7F 8A 3", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "70 7F 8A 4", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "Replicate a user-initiated fast-on (COC DFON) from Mini-Remote D to Mini-Remote C only during weekday daytime to avoid accidental triggers outside work hours. Uses device-level control event DFON and applies an allowed write command (WDU) to C."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "When C becomes On at sunset+10m -> Set F off",
    "parent": 0,
    "comment": "On COS of C to On combined with sunset offset, turn F off",
    "if": [
      { "==": { "device": "70 7F 8A 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 600 } }
    ],
    "then": [
      { "device": "70 7F 8A 5", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "Detect a change-of-state (COS) of Mini-Remote C to On (ST=100) and, when that coincides with sunset plus 10 minutes, issue a write change to Mini-Remote F to set it off. Uses ST property comparison and exact sunset offset."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 103,
    "name": "Nightly recovery: if F not responding at 22:00 attempt WDU",
    "parent": 0,
    "comment": "Daily check to attempt recovery on non-responding Mini-Remote F",
    "if": [
      { "at": { "time": "22:00" } },
      { "and": 1 },
      { "==": { "device": "70 7F 8A 5", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "70 7F 8A 5", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "Run a daily scheduled check at 22:00 and, if Mini-Remote F reports Responding=False (ERR=1), call WDU to attempt a write/recover action. Uses schedule + property equality to target only non-responding devices."
  }
}
__END_NUCORE_ROUTINE__
