__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Notify UD Mobile when Dining Table light activates at sunset",
    "parent": 0,
    "comment": "Trigger on physical control (fast on) or status change to on, but only at sunset.",
    "if": [
      { "(": 1 },
        { "==": { "device": "40 16 65 1", "control": "DFON" } },
        { "or": 1 },
        { ">": { "device": "40 16 65 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      {
        "device": "n001_udmobile",
        "command": "GV10",
        "parameters": [
          { "name": "Group", "value": 0, "uom": "25", "precision": 0 },
          { "name": "Sound", "value": 6, "uom": "25", "precision": 0 },
          { "name": "Content", "value": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Combine a COC (fast-on control) and a COS (status > 0) as alternatives, then restrict to sunset. Action sends UD Mobile Send Message using available Group and Sound enums. Content uses default numeric placeholder since Content is defined but not enumerated in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
