__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Weekday evening music -> set player and closet light",
    "parent": 0,
    "comment": "When CD player starts playing on weekdays after 18:00, unmute, set volume to 30%, and turn on closet light.",
    "if": [
      { "==": { "device": "n002_b8f80e4", "status": "ST", "value": 1, "uom": "25", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "n002_b8f80e4", "command": "PMUTE", "parameters": [ { "name": "n/a", "value": 0, "uom": "2", "precision": 0 } ] },
      { "device": "n002_b8f80e4", "command": "SVOL", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "43 5A A 1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger: CD player COS to Playing (value 1) AND weekday evening schedule. Actions: unmute (PMUTE=0) and set SVOL to 30% using the CD player's accepted commands and uom id=51; then turn on Master_closet_right (DON). Routine keeps simple direct actions for reliability."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1002,
    "name": "Closet fast-on -> set backlight",
    "parent": 0,
    "comment": "If Master_closet_right is fast-turned on (DFON), set backlight to 50% immediately.",
    "if": [
      { "==": { "device": "43 5A A 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "43 5A A 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "COC: detect DFON control on Master_closet_right and set the Backlight (BL) parameter to 50% using the device's accepted BL command and uom id=51. Immediate response to physical control."
  }
}
__END_NUCORE_ROUTINE__
