__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Button1 FastOn during weekday work hours -> Query Button2",
    "parent": 0,
    "comment": "Fast On (DFON) on Scene Button 1 during weekdays triggers a Query to Scene Button 2; otherwise update Mini Remote",
    "if": [
      { "==": { "device": "ZY009_201N001", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY009_201N002", "command": "QUERY" }
    ],
    "else": [
      { "device": "26 33 AB 1", "command": "WDU" }
    ],
    "reasoning": "Use COC (DFON) from Scene Button 1 combined with a weekly schedule (work hours). If triggered during those hours, poll Scene Button 2 via its accepted QUERY command; otherwise apply Write Changes to the Mini Remote (WDU) as a fallback action. Commands chosen only from each device's Accepts list."
  }
}
__END_NUCORE_ROUTINE__
