__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Evening porch motion: alert and record",
    "parent": 0,
    "comment": "When Dusk.Dawn reports On in the evening window, produce an audible beep and write changes to Low Bat.",
    "if": [
      { "==": { "device": "25 A3 83 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "25 A3 83 2", "command": "BEEP" },
      { "device": "25 A3 83 3", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "Use a COS on the Dusk.Dawn device (ST == On). Restrict to a daily evening window using an exact time range. BEEP uses the device's accepted command to provide immediate audible feedback; WDU is used on the Low Bat child to ensure any necessary change/write action is performed when motion is detected at night."
  }
}
__END_NUCORE_ROUTINE__
