__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Auto-close garage if opened overnight",
    "parent": 0,
    "comment": "When garage status changes to On during overnight period, send Off to close",
    "if": [
      {">=": {"device": "ZY010_001_1", "status": "ST", "value": 100, "uom": "78", "precision": 0}},
      {"and": 1},
      {"from": {"time": "22:00", "to": {"time": "06:00", "day": 1}}}
    ],
    "then": [
      {"device": "ZY010_001_1", "command": "DOF", "parameters": []}
    ],
    "else": [],
    "reasoning": "Trigger on COS where garage Status becomes On (value 100) and only during the overnight window (22:00 to 06:00 next day). Action sends the Off command (DOF) to ZY010_001_1 to close the door." 
  }
}
__END_NUCORE_ROUTINE__
