__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Open garage from assoc press overnight",
    "parent": 0,
    "comment": "Open ZY013_1 when assoc 2 or 3 sends ON between 22:00-06:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY013_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY013_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY013_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC from the association nodes (DON) as physical button presses; restrict to overnight window and open the main garage door when pressed. Parent is root (0)."
  }
}
__END_NUCORE_ROUTINE__
