__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night triple-press: query motion sensor and write remote",
    "parent": 0,
    "comment": "Triple-press patio button between 22:00-06:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY017_201N001", "control": "DON3" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY015_1", "command": "QUERY" },
      { "device": "1C 5C 95 1", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "Use the ZY017 triple-press (DON3) as a COC trigger and restrict it to overnight hours with the from/to schedule. Then query the motion sensor to refresh state and send a Write Changes to the Mini Remote. All commands used exist in the devices' Accept/Send lists."
  }
}
__END_NUCORE_ROUTINE__
