__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening manual patio ON -> beep bedroom and turn off patio",
    "parent": 0,
    "comment": "When patio light is manually turned on in the evening, beep the outside bedroom door and turn the patio light off.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY017_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "19:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "28 F A7 1", "command": "BEEP", "parameters": [] },
      { "device": "ZY017_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (manual ON) for the patio light combined with a daily time window 19:00-23:00; then trigger the door beep and immediately send Off to the patio light."
  }
}
__END_NUCORE_ROUTINE__
