__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night - Recover New Climate when not responding",
    "parent": 0,
    "comment": "When New Climate reports Responding=False during night hours, query and push updates to setup and cameras",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY073_1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY073_1", "command": "QUERY", "parameters": [] },
      { "device": "n001_setup", "command": "UPDATE", "parameters": [] },
      { "device": "n001_nosync", "command": "UPDATE", "parameters": [] }
    ],
    "else": [],
    "reasoning": "If New Climate stops responding (ERR=1) during the overnight window (22:00-06:00), first query the device to prompt a status refresh, then push update commands to the system setup and camera sync to attempt automated recovery and resynchronization."
  }
}
__END_NUCORE_ROUTINE__
