__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday manual on -> query third device",
    "parent": 0,
    "comment": "When either family-room device is turned on during weekday daytime, poll the third device",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY016_252N003", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY016_252N004", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "ZY016_252N005", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "During weekday business hours, if anyone manually turns on either of the two family-room switches (COC DON), immediately query the third device to refresh its status for the automations that depend on it. Uses only allowed accept command QUERY."
  }
}
__END_NUCORE_ROUTINE__
