__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: re-enable Garage-Wake Up when it sleeps",
    "parent": 0,
    "comment": "If the Wake Up node is not awake at sunset, set a 2-hour wake interval and enable Keep Awake.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY046_199", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY046_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 2, "uom": "20", "precision": 0 } ] },
      { "device": "ZY046_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger at sunset and only act when the Wake Up node reports Is Awake = False (COS). SETIV sets the wake interval to 2 hours (uom 20 = hours) and DON with parameter On (100, uom 78) enables Keep Awake so the node remains awake through the evening."
  }
}
__END_NUCORE_ROUTINE__
