__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night button press sets driveway param 5",
    "parent": 0,
    "comment": "When Basic Assoc 3 is pressed ON during night hours, configure Driveway and write changes.",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "ZY075_252N003", "control": "DON" } }
    ],
    "then": [
      {
        "device": "ZY080_1",
        "command": "CONFIG",
        "parameters": [
          { "name": "Parameter Number", "value": 5, "uom": "107", "precision": 0 },
          { "name": "Parameter Value", "value": 1, "uom": "107", "precision": 0 }
        ]
      },
      {
        "device": "ZY080_1",
        "command": "WDU",
        "parameters": []
      }
    ],
    "else": [],
    "reasoning": "During the defined night window, a physical ON control (COC) from Basic Assoc 3 should immediately update Driveway configuration parameter 5 to 1 and persist the change with WDU."
  }
}
__END_NUCORE_ROUTINE__
