__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query Stoop when garage door opens weekday morning",
    "parent": 0,
    "comment": "When garage door opens between 07:00-09:00 Mon-Fri, query assoc device",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY005_379", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZY075_252N005", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Run Query on the associated Basic Assoc 5 whenever the garage door sensor reports On during the weekday morning commute window. Uses COS (status == On) combined with a weekly time window to limit activation."
  }
}
__END_NUCORE_ROUTINE__
