__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening: Button1 fast-press queries garage ghost",
    "parent": 0,
    "comment": "When ZY038_201N001 DFON between 18:00-23:00 send QUERY to garage ghost",
    "if": [
      { "==": { "device": "ZY038_201N001", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "ZY060_201N002", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from Scene Button 1 combined with a fixed evening time window to limit automated queries. The Garage Light Ghost accepts QUERY, so the routine issues QUERY to refresh its status only during that period."
  }
}
__END_NUCORE_ROUTINE__
