__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday double-tap sets fan light color",
    "parent": 0,
    "comment": "Double-tap main switch during weekday work hours sets hue and saturation; else turn Enhanced Hue Off",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZB12565_001_1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZB22458_001_106", "command": "MOVETO", "parameters": [ { "name": "HUE", "value": 200, "uom": "149", "precision": 0 }, { "name": "SAT", "value": 150, "precision": 0 }, { "name": "DUR", "value": 2, "precision": 0 } ] }
    ],
    "else": [
      { "device": "ZB22458_001_106", "command": "EHUE", "parameters": [ { "name": "n/a", "value": 0, "uom": "78", "precision": 0 } ] }
    ],
    "reasoning": "Use a COC (double-tap) combined with a weekday schedule so manual activation only applies during work hours. Then sets HUE and SAT via MOVETO; otherwise ensure EHUE is Off."
  }
}
__END_NUCORE_ROUTINE__
