__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "POffice DFON (evening) -> Turn on Leigh Office Groups to 3000K @ BR 200",
    "parent": 0,
    "comment": "Triggered by POffice physical fast-on control during evening hours",
    "if": [
      { "==": { "device": "60 44 D6 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n006_huegrp88", "command": "DON", "parameters": [] },
      { "device": "n006_huegrp88", "command": "SET_CTBR", "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 1000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
        ]
      },
      { "device": "n006_huegrp89", "command": "DON", "parameters": [] },
      { "device": "n006_huegrp89", "command": "SET_CTBR", "parameters": [
          { "name": "K", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "D", "value": 1000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from the POffice device during an evening schedule. When triggered, explicitly send DON to ensure groups are on then set color temperature and brightness via SET_CTBR using the device-supported parameters and uom ids (K: uom 26, D: uom 42, BR: uom 100)."
  }
}
__END_NUCORE_ROUTINE__
