__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night alarm cross-check on alarm event",
    "parent": 0,
    "comment": "If underheat or leak emits DON overnight, query water and leak alarms",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY039_168", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY039_169", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY039_305", "command": "QUERY", "parameters": [] },
      { "device": "ZY039_169", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Detect DON control events from either underheat or leak during the defined night window and immediately query the water and leak devices to confirm conditions. Uses COC triggers (DON) and a nightly schedule."
  }
}
__END_NUCORE_ROUTINE__
