__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening physical-on sets C and F full",
    "parent": 0,
    "comment": "When either C or F is physically turned on during evening hours, force both to 100%",
    "if": [
      { "(": 1 },
        { "==": { "device": "70 82 67 4", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "70 82 67 5", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "70 82 67 4", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "70 82 67 5", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from either device C or F during the daily evening window to immediately set both devices to full (ST=100) so manual control yields consistent brightness. WDU is used because it is the device's accepted write command."
  }
}
__END_NUCORE_ROUTINE__
