__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Set Gary RM backlight on Fast On",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "A 18 B 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "A 18 B 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 2 } ] },
      { "device": "A 18 B 1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from device A 18 B 1. On that control event set its Backlight (BL) to the maximum allowed value 100% (uom 51) and commit the change with WDU."
  }
}
__END_NUCORE_ROUTINE__
