__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Nightly front-door motion: query devices",
    "parent": 0,
    "comment": "On overnight motion, poll door and powder-room sensors",
    "if": [
      { "==": { "device": "n006_516986685_m", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n006_516986685_db", "command": "QUERY", "parameters": [] },
      { "device": "55 8E F2 1", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use the motion sensor's DON event as the COC trigger and restrict to the overnight window (22:00-06:00). When motion occurs in that window, query the front-door device and the powder-room door to collect current states for logging or further processing. No action otherwise."
  }
}
__END_NUCORE_ROUTINE__
