__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight Powder RM Door Open Alert",
    "parent": 0,
    "comment": "If Door.3 opens overnight, alert Heartbeat and refresh door status",
    "if": [
      { "==": { "device": "55 8E F2 3", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "55 8E F2 4", "command": "BEEP" },
      { "device": "55 8E F2 3", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger on COS: Door.3 status becomes On (100). Restrict to overnight period 22:00-06:00 (uses day:1 overnight form). Then send BEEP to the Heartbeat device to create a local audible alert and send QUERY to the door sensor to refresh its status in the system."
  }
}
__END_NUCORE_ROUTINE__
