__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Garage door opened -> Gary lights on + backlight",
    "parent": 0,
    "comment": "At sunset, if garage door status changes to On, enable Gary lights and set backlight to 80%.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "2B 31 F2 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "52 9 E4 1", "command": "DON" },
      { "device": "52 9 E4 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on sunset combined with a COS indicating the laundry garage door sensor status is On (value 100). Then use Gary device DON to turn lights on and BL with value 80 (uom 51) to set the backlight. Uses only accepts commands and uom defined in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
