__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Closet IR ON (overnight) -> Dim Sophia Bedroom",
    "parent": 0,
    "comment": "When closet IR is physically turned on overnight, set bedroom lights to a low level with a short ramp.",
    "if": [
      { "(": 1 },
        { "==": { "device": "25 A4 3F 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "25 A4 3F 1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "53 97 76 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 16, "uom": "25", "precision": 0 } ] },
      { "device": "53 97 76 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger for the closet IR (DFON or DON) but only during overnight hours. Set Ramp Rate enum 16 (34.0 seconds) on Sophia's Bedroom Lights, then send an On command to 20% to provide a gentle overnight illumination. Reasoning included per rules."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Closet IR OFF -> Turn Sophia Bedroom Off",
    "parent": 0,
    "comment": "When closet IR is turned off, turn bedroom lights off.",
    "if": [
      { "==": { "device": "25 A4 3F 1", "control": "DOF" } }
    ],
    "then": [
      { "device": "53 97 76 1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COC off on the closet IR should immediately turn off Sophia's Bedroom Lights to avoid leaving them on after closet activity."
  }
}
__END_NUCORE_ROUTINE__
