__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night DFON -> BL20 + Query Battery",
    "parent": 0,
    "comment": "When Powder Bath Fan is fast-turned on overnight, dim backlight and query battery level.",
    "if": [
      { "==": { "device": "33 6 EB 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "33 6 EB 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] },
      { "device": "2A FF 3C 3", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger on a COC event (DFON) from the Powder Bath Fan constrained to the overnight time window. Then set the fan backlight to 20% using the BL command (uom 51, range 0-100) and issue a QUERY to the Battery Level device to update its status. No actions on else."
  }
}
__END_NUCORE_ROUTINE__
