__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sync Entrance with Hallway ON (weekdays evenings)",
    "parent": 0,
    "comment": "When Hallway plug is manually turned on during weekdays 18:00-23:00, turn Entrance plug on.",
    "if": [
      { "==": { "device": "ZY011_1", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZY012_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC (control DON) from Hallway plug during weekday evenings to mirror an ON action to the Entrance plug." 
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Sync Entrance with Hallway OFF (weekdays evenings)",
    "parent": 0,
    "comment": "When Hallway plug is manually turned off during weekdays 18:00-23:00, turn Entrance plug off.",
    "if": [
      { "==": { "device": "ZY011_1", "control": "DOF" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZY012_1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "Use COC (control DOF) from Hallway plug during weekday evenings to mirror an OFF action to the Entrance plug." 
  }
}
__END_NUCORE_ROUTINE__
