__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night leak detected - query devices and force auth fail",
    "parent": 0,
    "comment": "When leak is detected at night query devices and force authorization fail on controller",
    "if": [
      { "==": { "device": "n011_84c787addcc00d", "status": "GV8", "value": 1, "uom": "2", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n011_84c787addcc00d", "command": "QUERY", "parameters": [] },
      { "device": "n011_controller", "command": "QUERY", "parameters": [] },
      { "device": "n011_controller", "command": "SETFAILED", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on a COS (Leak Detected becomes True) combined with an overnight schedule (22:00 to 06:00). The actions use only accepted commands: QUERY on the sensor and controller, and SETFAILED on the controller to force authorization fail. This executes immediately when the condition is met during the scheduled window."
  }
}
__END_NUCORE_ROUTINE__
