__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual-on sets secondary light to night color",
    "parent": 0,
    "comment": "When 18389918 is manually turned on overnight, make 85035132 a dim warm night light",
    "if": [
      { "==": { "device": "n004_18389918", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n004_85035132", "command": "DON", "parameters": [] },
      { "device": "n004_85035132", "command": "SET_BRI", "parameters": [ { "name": "n/a", "value": 102, "uom": "51", "precision": 1 } ] },
      { "device": "n004_85035132", "command": "SET_COLORID", "parameters": [ { "name": "R", "value": 255, "uom": "100", "precision": 1 }, { "name": "G", "value": 140, "uom": "100", "precision": 1 }, { "name": "B", "value": 80, "uom": "100", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "COC trigger: user physically turns on n004_18389918 (DON). Only act during night window (22:00-06:00) to avoid changing daytime behavior. Ensure n004_85035132 is turned on, then apply a dim brightness (102 ≈ 40% of 255) and warm RGB color using the device's SET_BRI and SET_COLORID commands with correct UOMs and precisions."
  }
}
__END_NUCORE_ROUTINE__
