__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Button B DFON after sunset -> set Button C backlight to 10",
    "parent": 0,
    "comment": "When Garage Lights Button- B is fast-on pressed after sunset, set Button C backlight to 10 (uom 25).",
    "if": [
      { "==": { "device": "15 C6 91 4", "control": "DFON" } },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "15 C6 91 5", "command": "BL", "parameters": [ { "name": "n/a", "value": 10, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on a COC event (DFON) from Button B and require the event to occur during evening (sunset to 23:00). Use the device's BL accept command to set Button C backlight to an integer level (uom 25). No Write Changes needed because BL is an accepted command.")
  }
}
__END_NUCORE_ROUTINE__
