__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Christmas at sunset if basement remote is off",
    "parent": 0,
    "comment": "Enable holiday lights at sunset only when basement remote is off to avoid duplicate lighting.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "3D 2F 5D 1", "status": "ST", "value": 0, "uom": "51" } }
    ],
    "then": [
      { "device": "49 28 D5 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Schedule trigger at sunset combined with a COS check of the basement remote status ensures Christmas lights only turn on when the basement remote is off. Uses the Christmas_Lights DON command with the On enum (100)."
  }
}
__END_NUCORE_ROUTINE__
