__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Query other garage devices when entry opens after sunset",
    "parent": 0,
    "comment": "When Garage Entry ST becomes On during the evening window, query the other garage devices to refresh state",
    "if": [
      { "from": { "sunset": 0, "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { "==": { "device": "54 5D 58 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "54 5E E5 1", "command": "QUERY", "parameters": [] },
      { "device": "54 5E E5 3", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a time window from sunset until 23:00 and a COS (ST==On) on the Garage Entry sensor. When triggered, send QUERY to both downstream devices so their reported states are refreshed for downstream automations."
  }
}
__END_NUCORE_ROUTINE__
